mapper.select:
SELECT
FROM sys_permission sp WHERE sp.available = 1 AND sp.pid = #{pid,jdbcType=INTEGER}
AND sp.type = #{type,jdbcType=VARCHAR}
AND sp.id IN ( SELECT srp.permissionid FROM sys_role_permission srp WHERE srp.roleid IN ( SELECT roleid FROM sys_user_role sur WHERE sur.userid = #{userid,jdbcType=INTEGER} ) )
